import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext'; // Import the UserContext
import './Navbar.css';

const Navbar = () => {
  const { user, logoutUser } = useContext(UserContext);  // Get the user and logout function from context
  const navigate = useNavigate();

  const handleDashboardRedirect = () => {
    if (user?.role === 'USER') {
      navigate('/UserDashboard');
    } else if (user?.role === 'OWNER') {
      navigate('/RestaurantOwnerDashboard');
    }
  };

  const handleLogout = () => {
    logoutUser();
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1>Restaurant Orders Portal</h1>
      </div>
      <div className="navbar-right">
        <Link to="/contact" className='btn'>Contact Us</Link>
        {!user ? (
          <>
            <Link to="/login" className="btn">Login</Link>
            <Link to="/register" className="btn">Register</Link>
          </>
        ) : (
          <>
            <button className="btn user-icon" onClick={handleDashboardRedirect}>
              <i className="fas fa-user"></i> {/* User icon */}
            </button>
            <button className="btn logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
