import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
// import './ViewCart.css';

const ViewCart = () => {
  const { userId, restaurantId } = useParams();
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:200/cart/user/${userId}/restaurant/${restaurantId}`)
      .then((response) => response.json())
      .then((data) => setCartItems(data))
      .catch((error) => console.error('Error fetching cart items:', error));
  }, [userId, restaurantId]);

  const handleQuantityChange = (cartId, quantityChange) => {
    fetch(`http://localhost:200/cart/update/${cartId}?quantityChange=${quantityChange}`, {
      method: 'PUT',
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Cart updated:', data);
        // Refresh cart items
        setCartItems((prevItems) =>
          prevItems.map((item) =>
            item.cartId === cartId ? { ...item, quantity: item.quantity + quantityChange } : item
          )
        );
      })
      .catch((error) => console.error('Error updating cart quantity:', error));
  };

  return (
    <div className="view-cart">
      <Navbar />
      <h2>Your Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <li key={item.cartId}>
            {item.foodItemName} - Rs. {item.price} - Quantity: {item.quantity}
            <button onClick={() => handleQuantityChange(item.cartId, -1)}>-</button>
            <button onClick={() => handleQuantityChange(item.cartId, 1)}>+</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewCart;
