import React, { useEffect, useState, useContext } from 'react'; 
import { UserContext } from './context/UserContext'; // Import UserContext
import Navbar from './Navbar';
import { useNavigate, useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './ViewCart.css';

const ViewCart = () => {
  const { user } = useContext(UserContext); // Get user from context
  const { restaurantId } = useParams();
  const [cartItems, setCartItems] = useState([]);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddressId, setSelectedAddressId] = useState(null);
  const [showAddAddressForm, setShowAddAddressForm] = useState(false); // Toggle for add address form
  const [newAddress, setNewAddress] = useState({
    street: '',
    city: '',
    state: '',
    country: '',
    pinCode: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    if (!user) return; // Ensure user is available

    // Fetch cart items and their images in a single operation
    const fetchCartItems = async () => {
      try {
        const cartResponse = await fetch(`http://localhost:200/cart/user/${user.id}/restaurant/${restaurantId}`);
        const cartData = await cartResponse.json();

        const cartItemsWithImages = await Promise.all(
          cartData.map(async (item) => {
            const imageResponse = await fetch(`http://localhost:300/foodItems/${item.foodItemId}/image`);
            const imageBlob = await imageResponse.blob();
            const imageUrl = URL.createObjectURL(imageBlob);

            return { ...item, imageUrl }; // Add imageUrl to each cart item
          })
        );

        setCartItems(cartItemsWithImages);
      } catch (error) {
        console.error('Error fetching cart items or images:', error);
      }
    };

    fetchCartItems();

    // Fetch user addresses
    fetch(`http://localhost:100/addresses/user/${user.id}`)
      .then((response) => response.json())
      .then((data) => {
        setAddresses(data);
        if (data.length > 0) {
          setSelectedAddressId(data[0].id);
        }
      })
      .catch((error) => console.error('Error fetching addresses:', error));
  }, [user, restaurantId]);

  const handleQuantityChange = (cartId, quantityChange) => {
    fetch(`http://localhost:200/cart/update/${cartId}?quantityChange=${quantityChange}`, {
      method: 'PUT',
    })
      .then((response) => response.json())
      .then((data) => {
        setCartItems((prevItems) =>
          prevItems.map((item) =>
            item.cartId === cartId ? { ...item, quantity: item.quantity + quantityChange } : item
          )
        );
      })
      .catch((error) => console.error('Error updating cart quantity:', error));
  };

  const handlePlaceOrder = () => {
    if (!selectedAddressId) {
      alert('Please select a delivery address');
      return;
    }

    const orderData = {
      userId: user.id, // Use user.id from context
      restaurantId,
      addressId: selectedAddressId,
      cartItems: cartItems.map((item) => ({
        foodItemId: item.foodItemId,
        quantity: item.quantity,
        price: item.price,
      })),
    };

    fetch(`http://localhost:200/orders/place`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(orderData),
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errData) => {
            throw new Error(errData.message || "Something went wrong");
          });
        }
        return response.json();
      })
      .then((data) => {
        setOrderPlaced(true);
        toast.success(data.message);
        setTimeout(() => {
          navigate("/UserDashboard")
        }, 2000);
      })
      .catch((error) => {
        toast.error(error.message);
      });
  };

  const validateAddress = () => {
    const { street, city, state, country, pinCode } = newAddress;

    if (!street || street.length < 3) {
      toast.error('Street must be at least 3 characters long');
      return false;
    }
    if (!city || city.length < 2) {
      toast.error('City must be at least 2 characters long');
      return false;
    }
    if (!state || state.length < 2) {
      toast.error('State must be at least 2 characters long');
      return false;
    }
    if (!country || country.length < 2) {
      toast.error('Country must be at least 2 characters long');
      return false;
    }
    const pinCodePattern = /^[1-9][0-9]{5}$/;
    if (!pinCodePattern.test(pinCode)) {
      toast.error('Invalid pin code. It should be a 6-digit number starting with a non-zero digit');
      return false;
    }

    return true;
  };

  const handleAddAddress = () => {
    if (!validateAddress()) return; // Stop execution if address is invalid

    const addressData = {
      ...newAddress,
      userId: user.id
    };

    fetch(`http://localhost:100/addresses/add`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(addressData),
    })
      .then((response) => response.json())
      .then((data) => {
        setAddresses([...addresses, data]); // Update addresses list
        setShowAddAddressForm(false); // Close form after submission
        toast.success(data.message);
      })
      .catch((error) => {
        console.error('Error adding address:', error);
        toast.error(error.message);
      });
  };

  return (
    <div className="view-cart">
      <Navbar />
      <h2>Your Cart</h2>
      {orderPlaced ? (
        <p>Order placed successfully!</p>
      ) : (
        <>
          <div className="cart-container">
            {cartItems.length > 0 ? (
              cartItems.map((item) => (
                <div key={item.cartId} className="cart-card">
                  {item.imageUrl && <img src={item.imageUrl} alt={item.foodItemName} className="food-image" />}
                  <div className="cart-details">
                    <h4>{item.foodItemName}</h4>
                    <p>Price: Rs. {item.price}</p>
                    <div className="quantity-controls">
                      <button onClick={() => handleQuantityChange(item.cartId, -1)} disabled={item.quantity <= 1}>
                        -
                      </button>
                      <span>{item.quantity}</span>
                      <button onClick={() => handleQuantityChange(item.cartId, 1)}>+</button>
                    </div>
                    <p>Total: Rs. {item.price * item.quantity}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>Your cart is empty</p>
            )}
          </div>

          {cartItems.length > 0 && (
            <>
              <div className="address-section">
                <h3>Select Delivery Address:</h3>
                {addresses.length > 0 ? (
                  <>
                    <select value={selectedAddressId} onChange={(e) => setSelectedAddressId(e.target.value)}>
                      {addresses.map((address) => (
                        <option key={address.id} value={address.id}>
                          {address.street}, {address.city}, {address.pinCode}
                        </option>
                      ))}
                    </select>
                    <br />
                    <button onClick={() => setShowAddAddressForm(true)} className="add-address-button">
                      Add Address
                    </button>
                  </>
                ) : (
                  <>
                    <p>No address found</p>
                    <button onClick={() => setShowAddAddressForm(true)} className="add-address-button">
                      Add Address
                    </button>
                  </>
                )}
              </div>

              {showAddAddressForm && (
                <div className="add-address-form">
                  <h3>Add New Address</h3>
                  <input
                    type="text"
                    placeholder="Street"
                    value={newAddress.street}
                    onChange={(e) => setNewAddress({ ...newAddress, street: e.target.value })}
                  />
                  <br />
                  <input
                    type="text"
                    placeholder="City"
                    value={newAddress.city}
                    onChange={(e) => setNewAddress({ ...newAddress, city: e.target.value })}
                  />
                  <br />

                  <input
                    type="text"
                    placeholder="State"
                    value={newAddress.state}
                    onChange={(e) => setNewAddress({ ...newAddress, state: e.target.value })}
                  />
                  <br />

                  <input
                    type="text"
                    placeholder="Country"
                    value={newAddress.country}
                    onChange={(e) => setNewAddress({ ...newAddress, country: e.target.value })}
                  />
                  <br />

                  <input
                    type="text"
                    placeholder="Pin Code"
                    value={newAddress.pinCode}
                    onChange={(e) => setNewAddress({ ...newAddress, pinCode: e.target.value })}
                  />
                  <br />

                  <button onClick={handleAddAddress}>Add Address</button>
                </div>
              )}

              <button onClick={handlePlaceOrder} className="place-order-button">
                Place Order
              </button>
            </>
          )}
        </>
      )}
      <ToastContainer />
    </div>
  );
};

export default ViewCart;
