import React, { useEffect, useState, useContext } from 'react';
import { UserContext } from './context/UserContext'; // Import UserContext
import Navbar from './Navbar';
import { useParams } from 'react-router-dom';
import './ViewCart.css';

const ViewCart = () => {
  const { user } = useContext(UserContext); // Get user from context
  const { restaurantId } = useParams();
  const [foodCategories, setFoodCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);
  const [foodItems, setFoodItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddressId, setSelectedAddressId] = useState(null);

  useEffect(() => {
    if (!user) return; // Ensure user is available

    // Fetch food categories by restaurantId
    fetch(`http://localhost:300/foodCategories/restaurant/${restaurantId}`)
      .then((response) => response.json())
      .then((data) => {
        setFoodCategories(data);
      })
      .catch((error) => console.error('Error fetching food categories:', error));

    // Fetch user addresses
    fetch(`http://localhost:100/addresses/user/${user.id}`)
      .then((response) => response.json())
      .then((data) => {
        setAddresses(data);
        if (data.length > 0) {
          setSelectedAddressId(data[0].id);
        }
      })
      .catch((error) => console.error('Error fetching addresses:', error));
  }, [user, restaurantId]);

  // Fetch food items by selected category
  useEffect(() => {
    if (selectedCategoryId) {
      fetch(`http://localhost:300/foodItems/getFoodItem/${selectedCategoryId}`)
        .then((response) => response.json())
        .then((data) => {
          setFoodItems(data);
        })
        .catch((error) => console.error('Error fetching food items:', error));
    }
  }, [selectedCategoryId]);

  const handleAddToCart = (foodItem) => {
    fetch(`http://localhost:200/cart/add`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId: user.id,
        restaurantId,
        foodItemId: foodItem.foodItemId,
        quantity: 1,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setCartItems([...cartItems, { ...foodItem, cartId: data.cartId, quantity: 1 }]);
      })
      .catch((error) => console.error('Error adding to cart:', error));
  };

  const handleQuantityChange = (cartId, quantityChange) => {
    fetch(`http://localhost:200/cart/update/${cartId}?quantityChange=${quantityChange}`, {
      method: 'PUT',
    })
      .then((response) => response.json())
      .then((data) => {
        setCartItems((prevItems) =>
          prevItems.map((item) =>
            item.cartId === cartId ? { ...item, quantity: item.quantity + quantityChange } : item
          )
        );
      })
      .catch((error) => console.error('Error updating cart quantity:', error));
  };

  const handlePlaceOrder = () => {
    if (!selectedAddressId) {
      alert('Please select a delivery address');
      return;
    }

    const orderData = {
      userId: user.id,
      restaurantId,
      addressId: selectedAddressId,
      cartItems: cartItems.map((item) => ({
        foodItemId: item.foodItemId,
        quantity: item.quantity,
        price: item.price,
      })),
    };

    fetch(`http://localhost:200/orders/place`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(orderData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Order placed:', data);
        setOrderPlaced(true);
      })
      .catch((error) => console.error('Error placing order:', error));
  };

  return (
    <div className="view-cart">
      <Navbar />
      <h2>Your Cart</h2>
      {orderPlaced ? (
        <p>Order placed successfully!</p>
      ) : (
        <>
          {/* Food category selection */}
          <div className="category-section">
            <h3>Select a Food Category:</h3>
            <select
              value={selectedCategoryId}
              onChange={(e) => setSelectedCategoryId(e.target.value)}
            >
              <option value="" disabled>
                Select Category
              </option>
              {foodCategories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          {/* Display food items for selected category */}
          {selectedCategoryId && (
            <div className="food-items-container">
              <h3>Food Items</h3>
              {foodItems.length > 0 ? (
                foodItems.map((item) => (
                  <div key={item.foodItemId} className="food-item-card">
                    <h4>{item.foodItemName}</h4>
                    <p>Price: Rs. {item.price}</p>
                    <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
                  </div>
                ))
              ) : (
                <p>No food items available for this category.</p>
              )}
            </div>
          )}

          {/* Cart display */}
          {cartItems.length > 0 && (
            <div className="cart-container">
              {cartItems.map((item) => (
                <div key={item.cartId} className="cart-card">
                  <h4>{item.foodItemName}</h4>
                  <p>Price: Rs. {item.price}</p>
                  <div className="quantity-controls">
                    <button
                      onClick={() => handleQuantityChange(item.cartId, -1)}
                      disabled={item.quantity <= 1}
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleQuantityChange(item.cartId, 1)}>+</button>
                  </div>
                  <p>Total: Rs. {item.price * item.quantity}</p>
                </div>
              ))}
            </div>
          )}

          {cartItems.length > 0 && (
            <>
              <div className="address-section">
                <h3>Select Delivery Address:</h3>
                <select value={selectedAddressId} onChange={(e) => setSelectedAddressId(e.target.value)}>
                  {addresses.map((address) => (
                    <option key={address.id} value={address.id}>
                      {address.street}, {address.city}, {address.pinCode}
                    </option>
                  ))}
                </select>
              </div>

              <div className="place-order-section">
                <button onClick={handlePlaceOrder} className="place-order-button">
                  Place Order
                </button>
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default ViewCart;
