import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState({});
  const navigate = useNavigate();

  const validateEmail = (email) => /^[A-Za-z0-9._%+-]+@nucleusteq\.com$/.test(email);

  const validatePassword = (password) => /^(?=.*[0-9])(?=.*[a-zA-Z]).{4,}$/.test(password);

  const base64Encode = (str) => {
    return btoa(str);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const errors = {};

    if (!validateEmail(email)) {
      errors.email = 'Email is required and must end with @nucleusteq.com';
    }

    if (!validatePassword(password)) {
      errors.password = 'Password must contain at least 4 characters and 1 number';
    }

    if (Object.keys(errors).length > 0) {
      setError(errors);
      return;
    }
   const encodedPassword = base64Encode(password);
    try {
      const response = await fetch('http://localhost:100/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate(data.role === 'USER' ? '/user-dashboard' : '/restaurant-dashboard');
      } else {
        setError({ server: data.message || 'Login failed' });
      }
    } catch (error) {
      setError({ server: 'An error occurred. Please try again.' });
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error.server && <p className="error">{error.server}</p>}
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {error.email && <p className="error">{error.email}</p>}
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {error.password && <p className="error">{error.password}</p>}
        </div>
        <button type="submit" className="btn-submit">Login</button>
      </form>
      <p>
        If new user, then <Link to="/register">register here</Link>.
      </p>
    </div>
  );
};

export default LoginPage;
