import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewFoodItem.css';

const ViewFoodItem = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [categories, setCategories] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState('');
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const [newFoodItem, setNewFoodItem] = useState({
    foodItemName: '',
    description: '',
    price: '',
    isAvailable: false,
  });

  const userId = localStorage.getItem('userId');

  // Fetch restaurants by userId
  useEffect(() => {
    if (userId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/restaurant/restaurants/${userId}`)
        .then((response) => {
          setRestaurants(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching restaurants!", error);
          setError("Failed to load restaurants.");
          setLoading(false);
        });
    }
  }, [userId]);

  // Fetch categories by restaurant ID
  useEffect(() => {
    if (selectedRestaurantId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/categories/foodCategoryByRestaurantId/${selectedRestaurantId}`)
        .then((response) => {
          setCategories(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching categories!", error);
          setError("Failed to load categories.");
          setLoading(false);
        });
    }
  }, [selectedRestaurantId]);

  // Fetch food items by category ID
  useEffect(() => {
    if (selectedCategoryId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/foodItems/${selectedCategoryId}`)
        .then((response) => {
          setFoodItems(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching food items!", error);
          setError("Failed to load food items.");
          setLoading(false);
        });
    }
  }, [selectedCategoryId]);

  // Handle input change for the new food item
  const handleFoodItemChange = (e) => {
    const { name, value } = e.target;
    setNewFoodItem((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Submit new food item
  const handleAddFoodItem = (e) => {
    e.preventDefault();
    const foodItemData = {
      ...newFoodItem,
      categoryId: parseInt(selectedCategoryId),
      restaurantId: parseInt(selectedRestaurantId),
    };

    axios
      .post('http://localhost:300/foodItems/add', foodItemData)
      .then((response) => {
        setFoodItems([...foodItems, response.data]);
        // Reset the form after successful submission
        setNewFoodItem({
          foodItemName: '',
          description: '',
          price: '',
          isAvailable: false,
        });
        setError(null);
      })
      .catch((error) => {
        console.error("Error adding food item!", error);
        setError("Failed to add food item.");
      });
  };

  return (
    <div className="view-food-item-container">
      <h2>View and Add Food Items</h2>

      {loading && <p>Loading...</p>}
      {error && <div className="error-message">{error}</div>}

      {/* Restaurant Dropdown */}
      <div className="dropdown">
        <label htmlFor="restaurant">Select Restaurant:</label>
        <select
          id="restaurant"
          value={selectedRestaurantId}
          onChange={(e) => {
            setSelectedRestaurantId(e.target.value);
            setSelectedCategoryId(''); // Reset category when restaurant changes
            setCategories([]);
            setFoodItems([]);
          }}
        >
          <option value="">-- Select Restaurant --</option>
          {restaurants.map((restaurant) => (
            <option key={restaurant.restaurantId} value={restaurant.restaurantId}>
              {restaurant.restaurantName}
            </option>
          ))}
        </select>
      </div>

      {/* Category Dropdown */}
      {selectedRestaurantId && categories.length > 0 && (
        <div className="dropdown">
          <label htmlFor="category">Select Category:</label>
          <select
            id="category"
            value={selectedCategoryId}
            onChange={(e) => {
              setSelectedCategoryId(e.target.value);
              setFoodItems([]);
            }}
          >
            <option value="">-- Select Category --</option>
            {categories.map((category) => (
              <option key={category.foodCategoryId} value={category.foodCategoryId}>
                {category.foodCategoryName}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Food Items List */}
      {selectedCategoryId && (
        <div className="food-items-list">
          <h3>Food Items</h3>
          {foodItems.length > 0 ? (
            <ul>
              {foodItems.map((item) => (
                <li key={item.foodItemId}>
                  <strong>{item.foodItemName}</strong> - {item.description} - ${item.price}
                </li>
              ))}
            </ul>
          ) : (
            <p>No food items available for this category.</p>
          )}
        </div>
      )}

      {/* Add Food Item Form */}
      {selectedCategoryId && (
        <div className="add-food-item">
          <h3>Add New Food Item</h3>
          <form onSubmit={handleAddFoodItem}>
            <div>
              <label>Food Item Name:</label>
              <input
                type="text"
                name="foodItemName"
                value={newFoodItem.foodItemName}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={newFoodItem.description}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Price:</label>
              <input
                type="number"
                name="price"
                value={newFoodItem.price}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Is Available:</label>
              <input
                type="checkbox"
                name="isAvailable"
                checked={newFoodItem.isAvailable}
                onChange={(e) =>
                  setNewFoodItem((prev) => ({
                    ...prev,
                    isAvailable: e.target.checked,
                  }))
                }
              />
            </div>
            <button type="submit">Add Food Item</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ViewFoodItem;
