import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewFoodItem.css';

const ViewFoodItem = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [categories, setCategories] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState('');
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const [editingFoodItem, setEditingFoodItem] = useState(null); // For editing food items
  const [newFoodItem, setNewFoodItem] = useState({
    foodItemName: '',
    description: '',
    price: '',
    isAvailable: false,
    foodItemImage: null,
  });

  const userId = localStorage.getItem('userId');

  // Fetch restaurants based on user ID
  useEffect(() => {
    if (userId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/restaurant/restaurants/${userId}`)
        .then((response) => {
          setRestaurants(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching restaurants!", error);
          setError("Failed to load restaurants.");
          setLoading(false);
        });
    }
  }, [userId]);

  // Fetch categories based on selected restaurant ID
  useEffect(() => {
    if (selectedRestaurantId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/categories/foodCategory/${selectedRestaurantId}`)
        .then((response) => {
          setCategories(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching categories!", error);
          setError("Failed to load categories.");
          setLoading(false);
        });
    }
  }, [selectedRestaurantId]);

  // Fetch food items based on selected category ID
  useEffect(() => {
    if (selectedCategoryId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/foodItems/getFoodItem/${selectedCategoryId}`)
        .then((response) => {
          setFoodItems(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching food items!", error);
          setError("Failed to load food items.");
          setLoading(false);
        });
    }
  }, [selectedCategoryId]);

  // Handle food item changes
  const handleFoodItemChange = (e) => {
    const { name, value } = e.target;
    setNewFoodItem((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    setNewFoodItem((prev) => ({
      ...prev,
      foodItemImage: e.target.files[0],
    }));
  };

  const handleAddFoodItem = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('foodItemName', newFoodItem.foodItemName);
    formData.append('description', newFoodItem.description);
    formData.append('price', newFoodItem.price);
    formData.append('isAvailable', newFoodItem.isAvailable);
    formData.append('foodCategoryId', selectedCategoryId);
    formData.append('restaurantId', selectedRestaurantId);
    formData.append('foodItemImage', newFoodItem.foodItemImage);

    axios
      .post('http://localhost:300/foodItems/addFoodItem', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then((response) => {
        setFoodItems([...foodItems, response.data]);
        setNewFoodItem({
          foodItemName: '',
          description: '',
          price: '',
          isAvailable: false,
          foodItemImage: null,
        });
        setError(null);
      })
      .catch((error) => {
        console.error("Error adding food item!", error);
        setError("Failed to add food item.");
      });
  };

  // Function to handle edit button click
  const handleEditClick = (foodItem) => {
    setEditingFoodItem(foodItem);
    setNewFoodItem({
      foodItemName: foodItem.foodItemName,
      description: foodItem.description,
      price: foodItem.price,
      isAvailable: foodItem.isAvailable,
    });
  };

  // Function to handle update food item
  const handleUpdateFoodItem = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('foodItemName', newFoodItem.foodItemName);
    formData.append('description', newFoodItem.description);
    formData.append('price', newFoodItem.price);
    formData.append('isAvailable', newFoodItem.isAvailable);
    if (newFoodItem.foodItemImage) {
      formData.append('foodItemImage', newFoodItem.foodItemImage);
    }

    axios
      .put(`http://localhost:300/foodItems/updateFoodItem/${editingFoodItem.foodItemId}`, formData)
      .then((response) => {
        // Update the food items list after successful update
        setFoodItems(
          foodItems.map((item) =>
            item.foodItemId === editingFoodItem.foodItemId ? response.data : item
          )
        );
        setEditingFoodItem(null); // Clear editing state
        setNewFoodItem({
          foodItemName: '',
          description: '',
          price: '',
          isAvailable: false,
          foodItemImage: null,
        });
        setError(null);
      })
      .catch((error) => {
        console.error("Error updating food item!", error);
        setError("Failed to update food item.");
      });
  };

  return (
    <div className="view-food-item-container">
      <h2>View and Add Food Items</h2>

      {loading && <p>Loading...</p>}
      {error && <div className="error-message">{error}</div>}

      {/* Restaurant Dropdown */}
      <div className="dropdown">
        <label htmlFor="restaurant">Select Restaurant:</label>
        <select
          id="restaurant"
          value={selectedRestaurantId}
          onChange={(e) => {
            setSelectedRestaurantId(e.target.value);
            setSelectedCategoryId('');
            setCategories([]);
            setFoodItems([]);
          }}
        >
          <option value="">-- Select Restaurant --</option>
          {restaurants.map((restaurant) => (
            <option key={restaurant.restaurantId} value={restaurant.restaurantId}>
              {restaurant.restaurantName}
            </option>
          ))}
        </select>
      </div>

      {/* Category Dropdown */}
      {selectedRestaurantId && categories.length > 0 && (
        <div className="dropdown">
          <label htmlFor="category">Select Category:</label>
          <select
            id="category"
            value={selectedCategoryId}
            onChange={(e) => {
              setSelectedCategoryId(e.target.value);
              setFoodItems([]);
            }}
          >
            <option value="">-- Select Category --</option>
            {categories.map((category) => (
              <option key={category.foodCategoryId} value={category.foodCategoryId}>
                {category.foodCategoryName}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Food Item List */}
      <ul>
        {foodItems.map((item) => (
          <li key={item.foodItemId}>
            <img src={fetchFoodItemImage(item.foodItemId)} alt={item.foodItemName} />
            <p>{item.foodItemName}</p>
            <button onClick={() => handleEditClick(item)}>Edit</button>
          </li>
        ))}
      </ul>

      {/* Food Item Edit/Add Form */}
      <form onSubmit={editingFoodItem ? handleUpdateFoodItem : handleAddFoodItem}>
        <label>
          Name:
          <input
            type="text"
            name="foodItemName"
            value={newFoodItem.foodItemName}
            onChange={handleFoodItemChange}
            required
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={newFoodItem.description}
            onChange={handleFoodItemChange}
            required
          />
        </label>
        <label>
          Price:
          <input
            type="number"
            name="price"
            value={newFoodItem.price}
            onChange={handleFoodItemChange}
            required
          />
        </label>
        <label>
          Available:
          <input
            type="checkbox"
            name="isAvailable"
            checked={newFoodItem.isAvailable}
            onChange={(e) =>
              setNewFoodItem((prev) => ({
                ...prev,
                isAvailable: e.target.checked,
              }))
            }
          />
        </label>
        <label>
          Image:
          <input type="file" onChange={handleImageChange} />
        </label>
        <button type="submit">{editingFoodItem ? 'Update' : 'Add'} Food Item</button>
      </form>
    </div>
  );
}

export default ViewFoodItem;
