import React, { useState } from 'react';
import axios from 'axios';
import './AddRestaurant.css'; // Optional: You can add styles in this file

const AddRestaurant = ({ user }) => {
  const [restaurantName, setRestaurantName] = useState('');
  const [restaurantAddress, setRestaurantAddress] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [description, setDescription] = useState('');
  const [restaurantImage, setRestaurantImage] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [errors, setErrors] = useState({});
  const userId = localStorage.getItem('userId');

  const handleFileChange = (event) => {
    setRestaurantImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('userId', userId);
    formData.append('restaurantName', restaurantName);
    formData.append('restaurantAddress', restaurantAddress);
    formData.append('contactNumber', contactNumber);
    formData.append('description', description);
    formData.append('isOpen', isOpen);
    formData.append('restaurantImage', restaurantImage);

    axios.post('http://localhost:300/restaurant/addRestaurant', formData)
      .then(response => {
        console.log('Restaurant added successfully', response.data);
        // Reset form or navigate to a success page
        setErrors({});
      })
      .catch(error => {
        if (error.response && error.response.data) {
          setErrors(error.response.data); // Assuming the backend sends error messages in this format
        } else {
          console.error('There was an error adding the restaurant!', error);
        }
      });
  };

  return (
    <div className="add-restaurant-container">
      <h2>Add Restaurant</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="restaurantName">Restaurant Name:</label>
          <input
            type="text"
            id="restaurantName"
            value={restaurantName}
            onChange={(e) => setRestaurantName(e.target.value)}
            required
          />
          {errors.restaurantName && <p className="error-message">{errors.restaurantName}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="restaurantAddress">Restaurant Address:</label>
          <input
            type="text"
            id="restaurantAddress"
            value={restaurantAddress}
            onChange={(e) => setRestaurantAddress(e.target.value)}
            required
          />
          {errors.restaurantAddress && <p className="error-message">{errors.restaurantAddress}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="contactNumber">Contact Number:</label>
          <input
            type="text"
            id="contactNumber"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            pattern="^[9876]\d{9}$"
            title="Phone number must start with 9, 8, 7, or 6 and contain 10 digits"
            required
          />
          {errors.contactNumber && <p className="error-message">{errors.contactNumber}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
          {errors.description && <p className="error-message">{errors.description}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="isOpen">Is Open:</label>
          <input
            type="checkbox"
            id="isOpen"
            checked={isOpen}
            onChange={(e) => setIsOpen(e.target.checked)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="restaurantImage">Restaurant Image:</label>
          <input
            type="file"
            id="restaurantImage"
            accept="image/*"
            onChange={handleFileChange}
            required
          />
          {errors.restaurantImage && <p className="error-message">{errors.restaurantImage}</p>}
        </div>

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurant;
