import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const RestaurantDetails = () => {
  const { restaurantId } = useParams();
  const [restaurant, setRestaurant] = useState({});
  const [menuItems, setMenuItems] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [userId, setUserId] = useState(1); // Replace this with actual user ID
  const navigate = useNavigate();

  // Fetch restaurant details and menu items
  useEffect(() => {
    const fetchRestaurantDetails = async () => {
      try {
        const res = await axios.get(`http://localhost:200/restaurant/${restaurantId}`);
        setRestaurant(res.data);
        setMenuItems(res.data.menuItems); // Assuming menuItems are in restaurant data
      } catch (error) {
        console.error('Error fetching restaurant details:', error);
      }
    };

    fetchRestaurantDetails();
  }, [restaurantId]);

  // Handle adding item to cart
  const addToCart = async (foodItemId, price) => {
    try {
      const cartData = {
        userId: userId,
        restaurantId: parseInt(restaurantId),
        foodItemId: foodItemId,
        quantity: quantity,
        price: price,
      };

      const res = await axios.post('http://localhost:200/cart/add', cartData);
      alert(res.data.message); // Notify user of success
    } catch (error) {
      console.error('Error adding item to cart:', error);
      alert('Failed to add item to cart.');
    }
  };

  // Handle viewing cart
  const viewCart = () => {
    navigate(`/cart/${userId}/restaurant/${restaurantId}`);
  };

  return (
    <div>
      <h1>{restaurant.name}</h1>
      <p>{restaurant.description}</p>

      <h2>Menu</h2>
      <div>
        {menuItems.map(item => (
          <div key={item.foodItemId} style={{ marginBottom: '20px' }}>
            <h3>{item.name}</h3>
            <p>Price: {item.price}</p>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(parseInt(e.target.value))}
              min="1"
            />
            <button onClick={() => addToCart(item.foodItemId, item.price)}>Add to Cart</button>
          </div>
        ))}
      </div>

      <button onClick={viewCart}>View Cart</button>
    </div>
  );
};

export default RestaurantDetails;
