import React, { useState, useEffect } from 'react';
import './RestaurantOwnerDashboard.css';

const RestaurantOwnerDashboard = () => {
  const [activeSection, setActiveSection] = useState('profile');
  const [restaurantProfile, setRestaurantProfile] = useState({});
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [foodItems, setFoodItems] = useState([]);
  const [newFoodItem, setNewFoodItem] = useState({ name: '', price: '' });
  const [newRestaurant, setNewRestaurant] = useState({ name: '', address: '' });

  useEffect(() => {
    fetchRestaurantProfile();
    fetchCategories();
    fetchFoodItems();
  }, []);

  const fetchRestaurantProfile = async () => {
    const response = await fetch('http://localhost:100/restaurants/profile');
    const profile = await response.json();
    setRestaurantProfile(profile);
  };

  const fetchCategories = async () => {
    const response = await fetch('http://localhost:300/categories');
    const categories = await response.json();
    setCategories(categories);
  };

  const fetchFoodItems = async () => {
    const response = await fetch('http://localhost:300/food-items');
    const foodItems = await response.json();
    setFoodItems(foodItems);
  };

  const handleAddCategory = async () => {
    if (newCategory.trim() === '') return;

    const response = await fetch('http://localhost:300/categories', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: newCategory }),
    });

    if (response.ok) {
      setNewCategory('');
      fetchCategories();
    }
  };

  const handleAddFoodItem = async () => {
    if (newFoodItem.name.trim() === '' || newFoodItem.price.trim() === '') return;

    const response = await fetch('http://localhost:300/food-items', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newFoodItem),
    });

    if (response.ok) {
      setNewFoodItem({ name: '', price: '' });
      fetchFoodItems();
    }
  };

  const handleAddRestaurant = async () => {
    if (newRestaurant.name.trim() === '' || newRestaurant.address.trim() === '') return;

    const response = await fetch('http://localhost:100/restaurants', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newRestaurant),
    });

    if (response.ok) {
      setNewRestaurant({ name: '', address: '' });
      fetchRestaurantProfile();
    }
  };

  return (
    <div className="dashboard-container">
      <nav className="navbar">
        <button onClick={() => setActiveSection('profile')}>Profile</button>
        <button onClick={() => setActiveSection('categories')}>Categories</button>
        <button onClick={() => setActiveSection('addRestaurant')}>Add Restaurant</button>
        <button onClick={() => setActiveSection('foodMenu')}>Food Menu</button>
      </nav>

      {activeSection === 'profile' && (
        <div className="profile-section">
          <h3>Restaurant Profile</h3>
          <p><strong>Name:</strong> {restaurantProfile.name}</p>
          <p><strong>Address:</strong> {restaurantProfile.address}</p>
        </div>
      )}

      {activeSection === 'categories' && (
        <div className="categories-section">
          <h3>Categories</h3>
          <ul>
            {categories.map((category, index) => (
              <li key={index}>{category.name}</li>
            ))}
          </ul>
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="New Category"
          />
          <button onClick={handleAddCategory}>Add Category</button>
        </div>
      )}

      {activeSection === 'addRestaurant' && (
        <div className="add-restaurant-section">
          <h3>Add Restaurant</h3>
          <input
            type="text"
            value={newRestaurant.name}
            onChange={(e) => setNewRestaurant({ ...newRestaurant, name: e.target.value })}
            placeholder="Restaurant Name"
          />
          <input
            type="text"
            value={newRestaurant.address}
            onChange={(e) => setNewRestaurant({ ...newRestaurant, address: e.target.value })}
            placeholder="Restaurant Address"
          />
          <button onClick={handleAddRestaurant}>Add Restaurant</button>
        </div>
      )}

      {activeSection === 'foodMenu' && (
        <div className="food-menu-section">
          <h3>Food Items</h3>
          <ul>
            {foodItems.map((item, index) => (
              <li key={index}>
                {item.name} - ${item.price}
              </li>
            ))}
          </ul>
          <input
            type="text"
            value={newFoodItem.name}
            onChange={(e) => setNewFoodItem({ ...newFoodItem, name: e.target.value })}
            placeholder="New Food Item"
          />
          <input
            type="text"
            value={newFoodItem.price}
            onChange={(e) => setNewFoodItem({ ...newFoodItem, price: e.target.value })}
            placeholder="Price"
          />
          <button onClick={handleAddFoodItem}>Add Food Item</button>
        </div>
      )}
    </div>
  );
};

export default RestaurantOwnerDashboard;
