import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewAllCategories.css'; // Ensure you create appropriate styles

const ViewAllCategories = () => {
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [restaurantId, setRestaurantId] = useState(localStorage.getItem('restaurantId')); // Fetch from local storage or pass as prop

  useEffect(() => {
    // Fetch food categories by restaurant ID
    if (restaurantId) {
      axios.get(`http://localhost:100/categories/foodCategoryByRestaurantId/${restaurantId}`)
        .then(response => {
          setCategories(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the food categories!", error);
        });
    }
  }, [restaurantId]);

  const handleAddCategory = () => {
    if (newCategory.trim()) {
      axios.post('http://localhost:100/categories/addFoodCategory', {
        restaurantId: restaurantId,
        foodCategoryName: newCategory
      })
        .then(response => {
          setCategories([...categories, response.data]);
          setNewCategory(''); // Clear the input field
        })
        .catch(error => {
          console.error("There was an error adding the food category!", error);
        });
    }
  };

  return (
    <div className="view-all-categories-container">
      <h2>Food Categories</h2>

      <div className="add-category-form">
        <input
          type="text"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          placeholder="Enter new category name"
        />
        <button onClick={handleAddCategory}>Add Category</button>
      </div>

      <div className="categories-list">
        {categories.length === 0 ? (
          <p>No categories available.</p>
        ) : (
          <ul>
            {categories.map(category => (
              <li key={category.foodCategoryId}>
                {category.foodCategoryName}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ViewAllCategories;
