import React, { useState } from 'react';
import axios from 'axios';
import './AddRestaurant.css';

const AddRestaurant = ({ user }) => {
  const [restaurantName, setRestaurantName] = useState('');
  const [restaurantAddress, setRestaurantAddress] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [description, setDescription] = useState('');
  const [restaurantImage, setRestaurantImage] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  const [errors, setErrors] = useState({});  // State to store errors
  const userId = localStorage.getItem('userId');

  const handleFileChange = (event) => {
    setRestaurantImage(event.target.files[0]);
  };

  const validateForm = () => {
    const newErrors = {};

    // Validate restaurant name
    if (!restaurantName.trim()) {
      newErrors.restaurantName = 'Restaurant name is required';
    } else if (!/^[A-Za-z\s]+$/.test(restaurantName)) {
      newErrors.restaurantName = 'Restaurant name must contain only alphabets';
    }

    // Validate restaurant address
    if (!restaurantAddress.trim()) {
      newErrors.restaurantAddress = 'Restaurant address is required';
    }

    // Validate contact number
    if (!contactNumber.trim()) {
      newErrors.contactNumber = 'Contact number is required';
    } else if (!/^[9876]\d{9}$/.test(contactNumber)) {
      newErrors.contactNumber = 'Phone number must start with 9, 8, 7, or 6 and contain 10 digits';
    }

    // Validate description
    if (!description.trim()) {
      newErrors.description = 'Description is required';
    } else if (description.length > 255) {
      newErrors.description = 'Description cannot exceed 255 characters';
    }

    // Validate restaurant image
    if (!restaurantImage) {
      newErrors.restaurantImage = 'Restaurant image is required';
    } else if (!['image/jpeg', 'image/png'].includes(restaurantImage.type)) {
      newErrors.restaurantImage = 'Only JPG and PNG formats are allowed';
    }

    // Set errors
    setErrors(newErrors);

    // Return true if there are no errors
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!validateForm()) {
      return; // Stop form submission if validation fails
    }

    const formData = new FormData();
    formData.append('userId', userId);
    formData.append('restaurantName', restaurantName.trim());
    formData.append('restaurantAddress', restaurantAddress.trim());
    formData.append('contactNumber', contactNumber.trim());
    formData.append('description', description.trim());
    formData.append('isOpen', isOpen);
    formData.append('restaurantImage', restaurantImage);

    axios.post('http://localhost:300/restaurant/addRestaurant', formData)
      .then(response => {
        console.log('Restaurant added successfully', response.data);
        // Handle success (e.g., navigate to a different page or show a success message)
      })
      .catch(error => {
        console.error('There was an error adding the restaurant!', error);
      });
  };

  return (
    <div className="add-restaurant-container">
      <h2>Add Restaurant</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="restaurantName">Restaurant Name:</label>
          <input
            type="text"
            id="restaurantName"
            value={restaurantName}
            onChange={(e) => setRestaurantName(e.target.value)}
            required
          />
          {errors.restaurantName && <span className="error-message">{errors.restaurantName}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="restaurantAddress">Restaurant Address:</label>
          <input
            type="text"
            id="restaurantAddress"
            value={restaurantAddress}
            onChange={(e) => setRestaurantAddress(e.target.value)}
            required
          />
          {errors.restaurantAddress && <span className="error-message">{errors.restaurantAddress}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="contactNumber">Contact Number:</label>
          <input
            type="text"
            id="contactNumber"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            pattern="^[9876]\d{9}$"
            title="Phone number must start with 9, 8, 7, or 6 and contain 10 digits"
            required
          />
          {errors.contactNumber && <span className="error-message">{errors.contactNumber}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
          {errors.description && <span className="error-message">{errors.description}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="isOpen">Is Open:</label>
          <input
            type="checkbox"
            id="isOpen"
            checked={isOpen}
            onChange={(e) => setIsOpen(e.target.checked)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="restaurantImage">Restaurant Image:</label>
          <input
            type="file"
            id="restaurantImage"
            accept="image/*"
            onChange={handleFileChange}
            required
          />
          {errors.restaurantImage && <span className="error-message">{errors.restaurantImage}</span>}
        </div>

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurant;
