import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ViewCart = ({ match }) => {
  const { userId, restaurantId } = match.params; // Retrieve userId and restaurantId from route params
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get(`/cart/user/${userId}/restaurant/${restaurantId}`);
        setCartItems(response.data);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };
    fetchCartItems();
  }, [userId, restaurantId]);

  return (
    <div>
      <h1>Cart Items</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map(item => (
            <li key={item.cartId}>
              {item.foodItemId} - {item.quantity} x {item.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewCart;
