import React, { useState } from 'react';
import axios from 'axios';
import './RestaurantOwnerDashboard.css';
import { useNavigate } from 'react-router-dom';

const RestaurantOwnerDashboard = ({ user }) => {
  const [profileData, setProfileData] = useState(null);
  const [showProfilePanel, setShowProfilePanel] = useState(false);
  const navigate = useNavigate();

  const handleMyProfileClick = () => {
    setShowProfilePanel(!showProfilePanel);
    if (!showProfilePanel) {
      axios.get(`http://localhost:100/users/profile/${user.id}`)
        .then(response => {
          setProfileData(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the profile!", error);
        });
    }
  };

  const handleNavigation = (path) => {
    navigate(path, { state: { user: user } });
  };

  return (
    <div className="owner-dashboard-container">
      <nav className="navbar">
        <div className="navbar-left">Restaurant Owner Dashboard</div>
        <div className="navbar-right">
          <button onClick={() => handleNavigation('/')}>Home</button>
          <button onClick={() => handleNavigation('/login')}>Login</button>
          <button onClick={() => handleNavigation('/logout')}>Logout</button>
        </div>
      </nav>

      <aside className="sidebar">
        <h3>Actions</h3>
        <button onClick={handleMyProfileClick}>My Profile</button>
        <button onClick={() => handleNavigation('/addRestaurant')}>Add Restaurant</button>
        <button onClick={() => handleNavigation('/viewAllRestaurants')}>View All Restaurants</button>
        <button onClick={() => handleNavigation('/addCategory')}>Add Categories</button>
        <button onClick={() => handleNavigation('/viewAllRestaurantCategories')}>View All Restaurant Categories</button>
        <button onClick={() => handleNavigation('/updateCategory')}>Update Category</button>
        <button onClick={() => handleNavigation('/addFoodItem')}>Add Food Item</button>
        <button onClick={() => handleNavigation('/viewFoodItem')}>View Food Item</button>
        <button onClick={() => handleNavigation('/updateFoodItem')}>Update Food Item</button>
      </aside>

      <div className="content">
        {showProfilePanel && (
          <div className={`profile-panel ${showProfilePanel ? 'visible' : ''}`}>
            {profileData && (
              <div className="profile-details">
                <h2>Profile Information</h2>
                <p><strong>Name:</strong> {profileData.name}</p>
                <p><strong>Email:</strong> {profileData.email}</p>
                <p><strong>Phone Number:</strong> {profileData.phoneNo}</p>
                <p><strong>Role:</strong> {profileData.role}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantOwnerDashboard;
