import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();

  // Check if the user is logged in by retrieving user data from localStorage
  const userId = localStorage.getItem('userId');
  const userRole = localStorage.getItem('userRole'); // Assuming the role is saved during login

  const handleHomeClick = () => {
    if (userId && userRole) {
      // Redirect based on the role
      if (userRole === 'USER') {
        navigate('/user-dashboard');
      } else if (userRole === 'RESTAURANT_OWNER') {
        navigate('/restaurant-owner-dashboard');
      }
    } else {
      // Redirect to login if no user is logged in
      navigate('/login');
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1>Restaurant Orders Portal</h1>
      </div>
      <div className="navbar-right">
        {/* Conditionally render the Home button only if a user is logged in */}
        {userId ? (
          <button className="btn" onClick={handleHomeClick}>
            Home
          </button>
        ) : null}

        {/* Always show login/register if user is not logged in */}
        {!userId && (
          <>
            <Link to="/login" className="btn">Login</Link>
            <Link to="/register" className="btn">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
