import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ViewFoodItems.css';

const ViewFoodItems = ({ restaurantId }) => {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:100/foodItems/getFoodItems/${restaurantId}`)
      .then(response => {
        setFoodItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching food items:', error);
      });
  }, [restaurantId]);

  return (
    <div className="food-items-container">
      <h2>Food Items for Restaurant ID: {restaurantId}</h2>
      {foodItems.length > 0 ? (
        <ul>
          {foodItems.map((item) => (
            <li key={item.id}>
              <h3>{item.foodItemName}</h3>
              <p>{item.description}</p>
              <p>Price: ${item.price}</p>
              <p>Available: {item.isAvailable ? 'Yes' : 'No'}</p>
              {item.foodItemImage && (
                <img src={`data:image/jpeg;base64,${item.foodItemImage}`} alt={item.foodItemName} />
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No food items found.</p>
      )}
    </div>
  );
};

export default ViewFoodItems;
