import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext'; // Import UserContext
import './Navbar.css';

const Navbar = () => {
  const { user, logoutUser } = useContext(UserContext); // Get user and logoutUser from the context
  const navigate = useNavigate();

  const handleDashboardRedirect = () => {
    if (user && user.role === 'USER') {
      navigate('/UserDashboard');
    } else if (user && user.role === 'OWNER') {
      navigate('/RestaurantOwnerDashboard');
    }
  };

  const handleLogout = () => {
    logoutUser(); // Clear user data
    navigate('/'); // Redirect to homepage after logout
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1>Restaurant Orders Portal</h1>
      </div>
      <div className="navbar-right">
        {user ? (
          <>
            <Link to="/contact" className="btn">Contact Us</Link>
            <button onClick={handleDashboardRedirect} className="btn">
              <img 
                src="/path/to/user-icon.png" // Add path to user icon image
                alt="User Icon" 
                className="user-icon" 
              />
            </button>
            <button onClick={handleLogout} className="btn">Logout</button>
          </>
        ) : (
          <>
            <Link to="/contact" className="btn">Contact Us</Link>
            <Link to="/login" className="btn">Login</Link>
            <Link to="/register" className="btn">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
