import React, { useState } from 'react';
import axios from 'axios';
import './AddRestaurant.css';

const AddRestaurant = ({ user }) => {
  const [restaurantName, setRestaurantName] = useState('');
  const [restaurantAddress, setRestaurantAddress] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [description, setDescription] = useState('');
  const [restaurantImage, setRestaurantImage] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [errors, setErrors] = useState({});
  const userId = localStorage.getItem('userId');

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const validExtensions = ['image/jpeg', 'image/png'];
    if (file && !validExtensions.includes(file.type)) {
      setErrors({ ...errors, restaurantImage: 'Image must be a jpg or png file' });
    } else {
      setRestaurantImage(file);
      setErrors({ ...errors, restaurantImage: '' });
    }
  };

  const validateFields = () => {
    let validationErrors = {};

    // Validate restaurant name (only alphabets, non-blank)
    const nameRegex = /^[A-Za-z\s]+$/;
    if (!restaurantName.trim()) {
      validationErrors.restaurantName = 'Restaurant name cannot be blank';
    } else if (!nameRegex.test(restaurantName.trim())) {
      validationErrors.restaurantName = 'Restaurant name must contain only alphabets';
    }

    // Validate address (non-blank)
    if (!restaurantAddress.trim()) {
      validationErrors.restaurantAddress = 'Address cannot be blank';
    }

    // Validate contact number (must start with 9, 8, 7, or 6, and have 10 digits)
    const phoneRegex = /^[9876]\d{9}$/;
    if (!contactNumber.trim()) {
      validationErrors.contactNumber = 'Phone number cannot be blank';
    } else if (!phoneRegex.test(contactNumber.trim())) {
      validationErrors.contactNumber = 'Phone number must start with 9, 8, 7, or 6 and contain 10 digits';
    }

    // Validate description (non-blank and max 255 characters)
    if (!description.trim()) {
      validationErrors.description = 'Description cannot be blank';
    } else if (description.trim().length > 255) {
      validationErrors.description = 'Description cannot exceed 255 characters';
    }

    // Validate image
    if (!restaurantImage) {
      validationErrors.restaurantImage = 'Restaurant image is required';
    }

    return validationErrors;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const validationErrors = validateFields();
    setErrors(validationErrors);

    if (Object.keys(validationErrors).length === 0) {
      const formData = new FormData();
      formData.append('userId', userId);
      formData.append('restaurantName', restaurantName.trim());
      formData.append('restaurantAddress', restaurantAddress.trim());
      formData.append('contactNumber', contactNumber.trim());
      formData.append('description', description.trim());
      formData.append('isOpen', isOpen);
      formData.append('restaurantImage', restaurantImage);

      axios.post('http://localhost:300/restaurant/addRestaurant', formData)
        .then(response => {
          console.log('Restaurant added successfully', response.data);
          // Handle success (e.g., navigate to a different page or show a success message)
        })
        .catch(error => {
          console.error('There was an error adding the restaurant!', error);
        });
    }
  };

  return (
    <div className="add-restaurant-container">
      <h2>Add Restaurant</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="restaurantName">Restaurant Name:</label>
          <input
            type="text"
            id="restaurantName"
            value={restaurantName}
            onChange={(e) => setRestaurantName(e.target.value)}
            required
          />
          {errors.restaurantName && <p className="error">{errors.restaurantName}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="restaurantAddress">Restaurant Address:</label>
          <input
            type="text"
            id="restaurantAddress"
            value={restaurantAddress}
            onChange={(e) => setRestaurantAddress(e.target.value)}
            required
          />
          {errors.restaurantAddress && <p className="error">{errors.restaurantAddress}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="contactNumber">Contact Number:</label>
          <input
            type="text"
            id="contactNumber"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            pattern="^[9876]\d{9}$"
            title="Phone number must start with 9, 8, 7, or 6 and contain 10 digits"
            required
          />
          {errors.contactNumber && <p className="error">{errors.contactNumber}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            maxLength="255"
          ></textarea>
          {errors.description && <p className="error">{errors.description}</p>}
        </div>

        <div className="form-group">
          <label htmlFor="isOpen">Is Open:</label>
          <input
            type="checkbox"
            id="isOpen"
            checked={isOpen}
            onChange={(e) => setIsOpen(e.target.checked)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="restaurantImage">Restaurant Image (jpg or png):</label>
          <input
            type="file"
            id="restaurantImage"
            accept="image/jpeg, image/png"
            onChange={handleFileChange}
            required
          />
          {errors.restaurantImage && <p className="error">{errors.restaurantImage}</p>}
        </div>

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurant;
