import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RestaurantDetails = () => {
  const { restaurantId } = useParams(); // Get restaurant ID from the route params
  const userId = 1; // You might fetch this from user context or local storage
  const navigate = useNavigate();

  const [menuItems, setMenuItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [cartMessage, setCartMessage] = useState('');

  // Fetch restaurant menu details
  useEffect(() => {
    axios
      .get(`/api/restaurants/${restaurantId}/menu`)
      .then(response => {
        setMenuItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching restaurant menu', error);
      });
  }, [restaurantId]);

  // Handle adding item to cart
  const addToCart = (foodItemId, price) => {
    const cartItem = {
      userId: userId,
      restaurantId: parseInt(restaurantId),
      foodItemId: foodItemId,
      quantity: quantity,
      price: price * quantity
    };

    axios
      .post('/cart/add', cartItem)
      .then(response => {
        setCartMessage('Item added to cart successfully!');
      })
      .catch(error => {
        console.error('Error adding item to cart', error);
        setCartMessage('Failed to add item to cart.');
      });
  };

  // Handle view cart
  const viewCart = () => {
    navigate(`/user/${userId}/restaurant/${restaurantId}/cart`);
  };

  return (
    <div>
      <h1>Restaurant Menu</h1>
      {menuItems.length === 0 ? (
        <p>No menu items available.</p>
      ) : (
        <div>
          <ul>
            {menuItems.map(item => (
              <li key={item.foodItemId}>
                {item.name} - Rs. {item.price}
                <div>
                  <label htmlFor="quantity">Quantity: </label>
                  <input
                    type="number"
                    id="quantity"
                    value={quantity}
                    onChange={e => setQuantity(e.target.value)}
                    min="1"
                  />
                </div>
                <button
                  onClick={() => addToCart(item.foodItemId, item.price)}
                >
                  Add to Cart
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      <button onClick={viewCart}>View Cart</button>

      {cartMessage && <p>{cartMessage}</p>}
    </div>
  );
};

export default RestaurantDetails;
