import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewFoodItem.css';

const ViewFoodItem = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [categories, setCategories] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [error, setError] = useState(null);

  // New state for adding a food item
  const [newFoodItem, setNewFoodItem] = useState({
    foodItemName: '',
    description: '',
    price: '',
    isAvailable: false,
  });

  const userId = localStorage.getItem('userId'); 

  // Fetch restaurants when the component loads
  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:300/restaurant/restaurants/${userId}`)
        .then(response => {
          setRestaurants(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the restaurants!", error);
          setError("Failed to load restaurants.");
        });
    }
  }, [userId]);

  // Fetch categories when a restaurant is selected
  useEffect(() => {
    if (selectedRestaurant) {
      axios.get(`http://localhost:300/categories/foodCategoryByRestaurantId/${selectedRestaurant}`)
        .then(response => {
          setCategories(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the categories!", error);
          setError("Failed to load categories.");
        });
    }
  }, [selectedRestaurant]);

  // Fetch food items when a category is selected
  useEffect(() => {
    if (selectedCategory) {
      axios.get(`http://localhost:100/foodItems/${selectedCategory}`)
        .then(response => {
          setFoodItems(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the food items!", error);
          setError("Failed to load food items.");
        });
    }
  }, [selectedCategory]);

  // Handle food item input change
  const handleFoodItemChange = (e) => {
    const { name, value } = e.target;
    setNewFoodItem((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Submit new food item
  const handleAddFoodItem = (e) => {
    e.preventDefault();
    const foodItemData = {
      ...newFoodItem,
      categoryId: selectedCategory,
      restaurantId: selectedRestaurant,
    };

    axios.post('http://localhost:100/foodItems/add', foodItemData)
      .then(response => {
        // Optionally refresh the food item list after successful addition
        setFoodItems([...foodItems, response.data]);
        setNewFoodItem({
          foodItemName: '',
          description: '',
          price: '',
          isAvailable: false,
        });
        setError(null);
      })
      .catch(error => {
        console.error("There was an error adding the food item!", error);
        setError("Failed to add food item.");
      });
  };

  return (
    <div className="view-food-item-container">
      <h2>View and Add Food Items</h2>

      {error && <div className="error-message">{error}</div>}

      {/* Restaurant Dropdown */}
      <div className="dropdown">
        <label htmlFor="restaurant">Select Restaurant:</label>
        <select
          id="restaurant"
          value={selectedRestaurant}
          onChange={(e) => {
            setSelectedRestaurant(e.target.value);
            setCategories([]);
            setFoodItems([]);
          }}
        >
          <option value="">-- Select Restaurant --</option>
          {restaurants.map((restaurant) => (
            <option key={restaurant.id} value={restaurant.id}>
              {restaurant.restaurantName}
            </option>
          ))}
        </select>
      </div>

      {/* Category Dropdown */}
      {selectedRestaurant && categories.length > 0 && (
        <div className="dropdown">
          <label htmlFor="category">Select Category:</label>
          <select
            id="category"
            value={selectedCategory}
            onChange={(e) => {
              setSelectedCategory(e.target.value);
              setFoodItems([]);
            }}
          >
            <option value="">-- Select Category --</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.categoryName}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Food Items List */}
      {selectedCategory && (
        <div className="food-items-list">
          <h3>Food Items</h3>
          {foodItems.length > 0 ? (
            <ul>
              {foodItems.map((item) => (
                <li key={item.id}>
                  <strong>{item.foodItemName}</strong> - {item.description} - ${item.price}
                </li>
              ))}
            </ul>
          ) : (
            <p>No food items available for this category.</p>
          )}
        </div>
      )}

      {/* Add Food Item Form */}
      {selectedCategory && (
        <div className="add-food-item">
          <h3>Add New Food Item</h3>
          <form onSubmit={handleAddFoodItem}>
            <div>
              <label>Food Item Name:</label>
              <input
                type="text"
                name="foodItemName"
                value={newFoodItem.foodItemName}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={newFoodItem.description}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Price:</label>
              <input
                type="number"
                name="price"
                value={newFoodItem.price}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Is Available:</label>
              <input
                type="checkbox"
                name="isAvailable"
                checked={newFoodItem.isAvailable}
                onChange={(e) => setNewFoodItem((prevState) => ({
                  ...prevState,
                  isAvailable: e.target.checked,
                }))}
              />
            </div>
            <button type="submit">Add Food Item</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ViewFoodItem;
