import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewAllCategories.css';

const ViewAllCategories = ({ restaurantId }) => {
    const [categories, setCategories] = useState([]);
    const [newCategory, setNewCategory] = useState({ foodCategoryName: '' });

    useEffect(() => {
        if (restaurantId) {
            fetchCategories();
        }
    }, [restaurantId]);

    const fetchCategories = async () => {
        try {
            const response = await axios.get(`http://localhost:300/categories/foodCategoryByRestaurantId/${restaurantId}`);
            setCategories(response.data);
        } catch (error) {
            console.error('Error fetching categories:', error);
        }
    };

    const handleAddCategory = async () => {
        if (!restaurantId) {
            console.error('Restaurant ID is required');
            return;
        }

        const categoryToAdd = {
            restaurantId,
            foodCategoryName: newCategory.foodCategoryName
        };

        try {
            const response = await axios.post('http://localhost:300/categories/addFoodCategory', categoryToAdd);
            setCategories([...categories, response.data]);
            setNewCategory({ foodCategoryName: '' }); // Clear the input field after adding the category
        } catch (error) {
            console.error('Error adding category:', error);
        }
    };

    const handleUpdateCategory = async (id, updatedCategory) => {
        try {
            const response = await axios.put(`http://localhost:300/categories/foodCategoryByFoodCategoryId/${id}`, updatedCategory);
            const updatedCategories = categories.map((category) =>
                category.foodCategoryId === id ? response.data : category
            );
            setCategories(updatedCategories);
        } catch (error) {
            console.error('Error updating category:', error);
        }
    };

    return (
        <div className="view-all-categories-container">
            <h2>Food Categories</h2>
            <div className="add-category">
                <input
                    type="text"
                    value={newCategory.foodCategoryName}
                    onChange={(e) => setNewCategory({ foodCategoryName: e.target.value })}
                    placeholder="Add New Category"
                />
                <button onClick={handleAddCategory}>Add Category</button>
            </div>
            <div className="categories-list">
                {categories.map((category) => (
                    <div key={category.foodCategoryId} className="category-card">
                        <input
                            type="text"
                            value={category.foodCategoryName}
                            onChange={(e) =>
                                handleUpdateCategory(category.foodCategoryId, {
                                    ...category,
                                    foodCategoryName: e.target.value
                                })
                            }
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ViewAllCategories;
