import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewFoodItem.css';

const ViewFoodItem = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [categories, setCategories] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [allFoodItems, setAllFoodItems] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState('');
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [editingFoodItem, setEditingFoodItem] = useState(null); // Track the food item being edited

  const [newFoodItem, setNewFoodItem] = useState({
    foodItemName: '',
    description: '',
    price: '',
    isAvailable: false,
    foodItemImage: null,
  });

  const userId = localStorage.getItem('userId');

  // Fetch restaurants based on user ID
  useEffect(() => {
    if (userId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/restaurant/restaurants/${userId}`)
        .then((response) => {
          setRestaurants(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching restaurants!", error);
          setError("Failed to load restaurants.");
          setLoading(false);
        });
    }
  }, [userId]);

  // Fetch categories based on selected restaurant ID
  useEffect(() => {
    if (selectedRestaurantId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/categories/foodCategory/${selectedRestaurantId}`)
        .then((response) => {
          setCategories(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching categories!", error);
          setError("Failed to load categories.");
          setLoading(false);
        });
    }
  }, [selectedRestaurantId]);

  // Fetch food items based on selected category ID
  useEffect(() => {
    if (selectedCategoryId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/foodItems/getFoodItem/${selectedCategoryId}`)
        .then((response) => {
          setFoodItems(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching food items!", error);
          setError("Failed to load food items.");
          setLoading(false);
        });
    }
  }, [selectedCategoryId]);

  // Fetch all food items based on restaurant ID
  const fetchAllFoodItemsByRestaurant = () => {
    if (selectedRestaurantId) {
      setLoading(true);
      axios
        .get(`http://localhost:501/foodItems/getFoodItems/${selectedRestaurantId}`)
        .then((response) => {
          setAllFoodItems(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching all food items!", error);
          setError("Failed to load all food items.");
          setLoading(false);
        });
    }
  };

  const handleFoodItemChange = (e) => {
    const { name, value } = e.target;
    setNewFoodItem((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    setNewFoodItem((prev) => ({
      ...prev,
      foodItemImage: e.target.files[0],
    }));
  };

  const handleEditFoodItem = (item) => {
    setEditingFoodItem(item); // Set the item to be edited
  };

  const handleUpdateFoodItem = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('foodItemName', editingFoodItem.foodItemName);
    formData.append('description', editingFoodItem.description);
    formData.append('price', editingFoodItem.price);
    formData.append('isAvailable', editingFoodItem.isAvailable);
    formData.append('foodItemImage', editingFoodItem.foodItemImage);

    axios
      .put(`http://localhost:300/foodItems/update/${editingFoodItem.foodItemId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then((response) => {
        setFoodItems((prevItems) =>
          prevItems.map((item) =>
            item.foodItemId === editingFoodItem.foodItemId ? response.data : item
          )
        );
        setEditingFoodItem(null); // Clear editing mode
        setError(null);
      })
      .catch((error) => {
        console.error("Error updating food item!", error);
        setError("Failed to update food item.");
      });
  };

  const fetchFoodItemImage = (id) => {
    return `http://localhost:300/foodItems/${id}/image`;
  };

  return (
    <div className="view-food-item-container">
      <h2>View and Add Food Items</h2>

      {loading && <p>Loading...</p>}
      {error && <div className="error-message">{error}</div>}

      {/* Display All Food Items for Restaurant */}
      {allFoodItems.length > 0 && (
        <div className="all-food-items-list">
          <h3>All Food Items for Selected Restaurant</h3>
          <ul>
            {allFoodItems.map((item) => (
              <li key={item.foodItemId}>
                <img
                  src={fetchFoodItemImage(item.foodItemId)}
                  alt={item.foodItemName}
                  className="food-item-image"
                />
                <strong>{item.foodItemName}</strong> - {item.description} - ${item.price}
                <button onClick={() => handleEditFoodItem(item)}>Edit</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Edit Food Item Form */}
      {editingFoodItem && (
        <div className="edit-food-item">
          <h3>Edit Food Item</h3>
          <form onSubmit={handleUpdateFoodItem}>
            <div>
              <label>Food Item Name:</label>
              <input
                type="text"
                name="foodItemName"
                value={editingFoodItem.foodItemName}
                onChange={(e) =>
                  setEditingFoodItem({ ...editingFoodItem, foodItemName: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={editingFoodItem.description}
                onChange={(e) =>
                  setEditingFoodItem({ ...editingFoodItem, description: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Price:</label>
              <input
                type="number"
                name="price"
                value={editingFoodItem.price}
                onChange={(e) =>
                  setEditingFoodItem({ ...editingFoodItem, price: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label>Available:</label>
              <input
                type="checkbox"
                name="isAvailable"
                checked={editingFoodItem.isAvailable}
                onChange={(e) =>
                  setEditingFoodItem({ ...editingFoodItem, isAvailable: e.target.checked })
                }
              />
            </div>
            <div>
              <label>Food Item Image:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  setEditingFoodItem({ ...editingFoodItem, foodItemImage: e.target.files[0] })
                }
              />
            </div>
            <button type="submit">Update Food Item</button>
            <button type="button" onClick={() => setEditingFoodItem(null)}>
              Cancel
            </button>
          </form>
        </div>
      )}

      {/* Other existing code... */}
    </div>
  );
};

export default ViewFoodItem;
