import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();

  // Fetch role and userId from localStorage
  const userId = localStorage.getItem('userId');
  const userRole = localStorage.getItem('role'); // Store 'USER' or 'OWNER' in localStorage

  // Function to handle redirection based on role
  const handleDashboardRedirect = () => {
    if (userRole === 'USER') {
      navigate(`/user-dashboard/${userId}`);
    } else if (userRole === 'OWNER') {
      navigate(`/restaurant-owner-dashboard/${userId}`);
    } else {
      console.error("No valid role found");
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1>Restaurant Orders Portal</h1>
      </div>
      <div className="navbar-right">
        <Link to="/login" className="btn">Login</Link>
        <Link to="/register" className="btn">Register</Link>
        {/* Show Go to Dashboard only if logged in */}
        {userId && (
          <button onClick={handleDashboardRedirect} className="btn">
            Go to Dashboard
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
