import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LoginPage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  let [password, setPassword] = useState('');
  const [error, setError] = useState({});
  const navigate = useNavigate();

  const validateEmail = (email) => {
    const re = /^[A-Za-z0-9._%+-]+@nucleusteq\.com$/;
    return re.test(String(email).toLowerCase());
  };

  const validatePassword = (password) => {
    const re = /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*()])[A-Za-z\d!@#$%^&*()]{6,}$/;
    return re.test(password);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const validationErrors = {};

    if (!email) {
      validationErrors.email = 'Email is required.';
    } else if (!validateEmail(email)) {
      validationErrors.email = 'Email must end with @nucleusteq.com.';
    }

    if (!password) {
      validationErrors.password = 'Password is required.';
    } else if (!validatePassword(password)) {
      validationErrors.password = 'Password must be at least 6 characters long and include at least one uppercase letter, one digit, and one special character.';
    }

    if (Object.keys(validationErrors).length > 0) {
      setError(validationErrors);
      return;
    }

    // Encode password
    const encodedPassword = btoa(password);

    try {
      const response = await axios.post('http://localhost:100/users/login', { email, password: encodedPassword });
      const userOutDto = response.data;
      onLoginSuccess(userOutDto); // Call onLoginSuccess with the received userOutDto
      const { role } = userOutDto;
      if (role === 'USER') {
        navigate('/userDashboard');
      } else if (role === 'OWNER') {
        navigate('/RestaurantOwnerDashboard');
      } else {
        setError('Invalid role received.');
      }
    } catch (err) {
      if (err.response && err.response.status === 404) {
        setError('Email mismatched.');
      } else if (err.response && err.response.status === 401) {
        setError('Password mismatched.');
      } else {
        setError('An error occurred.');
      }
    }
  };
  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {error && <p className="message">{error}</p>}
    </div>
  );
};
export default LoginPage;
