import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ViewFoodItem.css';

const ViewFoodItem = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [categories, setCategories] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [selectedRestaurantId, setSelectedRestaurantId] = useState('');
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editFoodItemId, setEditFoodItemId] = useState(null);

  const [newFoodItem, setNewFoodItem] = useState({
    foodItemName: '',
    description: '',
    price: '',
    isAvailable: false,
    foodItemImage: null,
  });

  const userId = localStorage.getItem('userId');

  // Fetch restaurants based on user ID
  useEffect(() => {
    if (userId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/restaurant/restaurants/${userId}`)
        .then((response) => {
          setRestaurants(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError("Failed to load restaurants.");
          setLoading(false);
        });
    }
  }, [userId]);

  // Fetch categories based on selected restaurant ID
  useEffect(() => {
    if (selectedRestaurantId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/categories/foodCategory/${selectedRestaurantId}`)
        .then((response) => {
          setCategories(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError("Failed to load categories.");
          setLoading(false);
        });
    }
  }, [selectedRestaurantId]);

  // Fetch food items based on selected category ID
  useEffect(() => {
    if (selectedCategoryId) {
      setLoading(true);
      axios
        .get(`http://localhost:300/foodItems/getFoodItem/${selectedCategoryId}`)
        .then((response) => {
          setFoodItems(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError("Failed to load food items.");
          setLoading(false);
        });
    }
  }, [selectedCategoryId]);

  // Handle food item changes
  const handleFoodItemChange = (e) => {
    const { name, value } = e.target;
    setNewFoodItem((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    setNewFoodItem((prev) => ({
      ...prev,
      foodItemImage: e.target.files[0],
    }));
  };

  // Populate form with the selected food item for editing
  const handleEditFoodItem = (item) => {
    setNewFoodItem({
      foodItemName: item.foodItemName,
      description: item.description,
      price: item.price,
      isAvailable: item.isAvailable,
      foodItemImage: null,  // Image will be updated if a new one is provided
    });
    setEditFoodItemId(item.foodItemId);
    setIsEditing(true);
  };

  // Handle updating an existing food item
  const handleUpdateFoodItem = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('foodItemName', newFoodItem.foodItemName);
    formData.append('description', newFoodItem.description);
    formData.append('price', newFoodItem.price);
    formData.append('isAvailable', newFoodItem.isAvailable);
    formData.append('foodCategoryId', selectedCategoryId);
    formData.append('restaurantId', selectedRestaurantId);

    if (newFoodItem.foodItemImage) {
      formData.append('foodItemImage', newFoodItem.foodItemImage);
    }

    axios
      .put(`http://localhost:300/foodItems/updateFoodItem/${editFoodItemId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then((response) => {
        const updatedItems = foodItems.map((item) =>
          item.foodItemId === editFoodItemId ? response.data : item
        );
        setFoodItems(updatedItems);
        setIsEditing(false);
        setError(null);
      })
      .catch((error) => {
        setError("Failed to update food item.");
      });
  };

  return (
    <div className="view-food-item-container">
      <h2>View and Add/Edit Food Items</h2>

      {loading && <p>Loading...</p>}
      {error && <div className="error-message">{error}</div>}

      {/* Edit Food Item Form */}
      {isEditing && (
        <div className="edit-food-item">
          <h3>Edit Food Item</h3>
          <form onSubmit={handleUpdateFoodItem}>
            <div>
              <label>Food Item Name:</label>
              <input
                type="text"
                name="foodItemName"
                value={newFoodItem.foodItemName}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={newFoodItem.description}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Price:</label>
              <input
                type="number"
                name="price"
                value={newFoodItem.price}
                onChange={handleFoodItemChange}
                required
              />
            </div>
            <div>
              <label>Available:</label>
              <input
                type="checkbox"
                name="isAvailable"
                checked={newFoodItem.isAvailable}
                onChange={(e) =>
                  setNewFoodItem((prev) => ({ ...prev, isAvailable: e.target.checked }))
                }
              />
            </div>
            <div>
              <label>Food Item Image:</label>
              <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
            <button type="submit">Update Food Item</button>
          </form>
        </div>
      )}

      {/* List of Food Items with Edit Button */}
      {foodItems.length > 0 && (
        <div className="food-items-list">
          <h3>Food Items for Selected Category</h3>
          <ul>
            {foodItems.map((item) => (
              <li key={item.foodItemId}>
                <strong>{item.foodItemName}</strong> - {item.description} - rs{item.price}
                <button onClick={() => handleEditFoodItem(item)}>Edit</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ViewFoodItem;
