import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles.css'; // Import the CSS file

const RestaurantOwnerDashboard = () => {
  const navigate = useNavigate();
  const [showForm, setShowForm] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('userId');
    navigate('/login');
  };

  const handleAddRestaurantClick = () => {
    setShowForm(true);
  };

  return (
    <div className="dashboard">
      <h1>Restaurant Owner Dashboard</h1>
      <nav>
        <button onClick={() => navigate('/profile')}>Profile</button>
        <button onClick={handleAddRestaurantClick}>Add Restaurant</button>
        <button onClick={() => navigate('/categories')}>Category</button>
        <button onClick={() => navigate('/food-menu')}>Food Menu</button>
      </nav>
      <button onClick={handleLogout}>Logout</button>

      {showForm && <AddRestaurantForm />}
    </div>
  );
};

const AddRestaurantForm = () => {
  const [formData, setFormData] = useState({
    restaurantName: '',
    restaurantAddress: '',
    contactNumber: '',
    description: '',
    restaurantImage: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      restaurantImage: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    for (let key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      const response = await fetch('/api/restaurants', {
        method: 'POST',
        body: formDataToSend,
      });

      if (response.ok) {
        alert('Restaurant added successfully!');
      } else {
        alert('Failed to add restaurant.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while adding the restaurant.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Restaurant</h2>
      <input
        type="text"
        name="restaurantName"
        placeholder="Restaurant Name"
        value={formData.restaurantName}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="restaurantAddress"
        placeholder="Restaurant Address"
        value={formData.restaurantAddress}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="contactNumber"
        placeholder="Contact Number"
        value={formData.contactNumber}
        onChange={handleChange}
        pattern="^[9876]\d{9}$"
        title="Phone number must start with 9, 8, 7, or 6 and contain 10 digits"
        required
      />
      <textarea
        name="description"
        placeholder="Description"
        value={formData.description}
        onChange={handleChange}
        required
      />
      <input
        type="file"
        name="restaurantImage"
        accept="image/*"
        onChange={handleFileChange}
        required
      />
      <input type="submit" value="Add Restaurant" />
    </form>
  );
};

export default RestaurantOwnerDashboard;
